mapper.select:
select custid,name,pwd,sex,identity,phone,career,address
from customer where identity=#{0}
order by custid

select custid,name,pwd,sex,identity,phone,career,address 
from customer
where name = #{0} and pwd = #{1}

mapper.update:
update customer
name=#{name}, pwd=#{pwd}, sex=#{sex}, identity=#{identity}, phone=#{phone}, career=#{career}, address=#{address}
custid = #{custId}
