mapper.update:
update renttable


		realEndTime = #{realEndTime},


		beginTime = #{beginTime},


		endTime = #{endTime},


		userName = #{userName}


where rentid=#{rentid}

mapper.select:
select rentid,carNumber,custIdentity,userName,rentNo,imprest,pricePayAble,realPay,beginTime,endTime,realEndTime,rentFlag
from renttable


		rentno = #{rentNo}


		realEndTime = #{realEndTime}


		beginTime = #{beginTime}


		endTime = #{endTime}


		rentFlag = #{rentFlag}


		userName = #{userName}


		carNumber = #{carNumber}


		custIdentity = #{custIdentity}


	ORDER BY rentFlag,rentid desc
